import React, { useState, useEffect } from 'react';
import { db } from '../firebase';
import { collection, addDoc, getDocs, orderBy, query } from 'firebase/firestore'; // Aquí incluimos query
import Comments from './Comments';

function Forum({ currentUser }) {
  const [threads, setThreads] = useState([]);
  const [newThreadTitle, setNewThreadTitle] = useState('');
  const [selectedThread, setSelectedThread] = useState(null);

  // Cargar los hilos desde Firestore
  const loadThreads = async () => {
    const threadsCollection = collection(db, 'threads');
    const q = query(threadsCollection, orderBy('timestamp', 'desc'));
    const querySnapshot = await getDocs(q);
    const threadsData = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setThreads(threadsData);
  };

  // Manejar la creación de un nuevo hilo
  const handleThreadSubmit = async (e) => {
    e.preventDefault();
    if (newThreadTitle.trim() === '') return;

    await addDoc(collection(db, 'threads'), {
      title: newThreadTitle,
      author: currentUser, // El usuario que está creando el hilo
      timestamp: new Date(),
    });

    setNewThreadTitle('');
    loadThreads(); // Recargar los hilos
  };

  useEffect(() => {
    loadThreads(); // Cargar hilos al montar el componente
  }, []);

  return (
    <div>
      <h2>Foro</h2>

      {/* Formulario para crear un nuevo hilo */}
      <form onSubmit={handleThreadSubmit}>
        <input
          type="text"
          value={newThreadTitle}
          onChange={(e) => setNewThreadTitle(e.target.value)}
          placeholder="Título del nuevo hilo"
        />
        <button type="submit">Crear Hilo</button>
      </form>

      {/* Lista de hilos disponibles */}
      <ul>
        {threads.map(thread => (
          <li key={thread.id}>
            <button onClick={() => setSelectedThread(thread.id)}>
              {thread.title} - por {thread.author}
            </button>
          </li>
        ))}
      </ul>

      {/* Mostrar comentarios del hilo seleccionado */}
      {selectedThread && <Comments threadId={selectedThread} currentUser={currentUser} />}
    </div>
  );
}

export default Forum;
