import React, { useEffect, useState } from 'react';
import { collection, addDoc, getDocs, orderBy, query, where } from 'firebase/firestore'; // Asegúrate de que estén todos los imports necesarios
import { db } from '../firebase';

function Comments({ threadId, currentUser }) {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');

  // Función para cargar los comentarios del hilo actual
  const loadComments = async () => {
    const commentsCollection = collection(db, 'comments');
    const q = query(commentsCollection, where('threadId', '==', threadId), orderBy('timestamp', 'desc')); // Filtra por el hilo actual
    const querySnapshot = await getDocs(q);
    const commentsData = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
    setComments(commentsData);
  };

  // Manejar el envío de un nuevo comentario
  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (newComment.trim() === '') return; // No enviar comentarios vacíos

    await addDoc(collection(db, 'comments'), {
      text: newComment,
      author: currentUser, // Utiliza el nombre del usuario autenticado
      timestamp: new Date(),
      threadId: threadId, // ID del hilo relacionado
    });

    setNewComment('');
    loadComments(); // Recargar comentarios después de agregar uno nuevo
  };

  useEffect(() => {
    loadComments(); // Cargar comentarios al montar el componente
  }, [threadId]);

  return (
    <div>
      <h2>Comentarios</h2>
      <form onSubmit={handleCommentSubmit}>
        <textarea
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          placeholder="Escribe un comentario..."
        />
        <button type="submit">Enviar</button>
      </form>
      <ul>
        {comments.map(comment => (
          <li key={comment.id}>
            <strong>{comment.author}</strong>: {comment.text}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Comments;
