import { useState, useEffect } from "react";
import { db } from "../firebase";
import { collection, addDoc, query, onSnapshot } from "firebase/firestore";
import Comments from './Comments';

function Forum() {
  
  const threadId = 'hilo_1'; // Esto debe ser dinámico según el hilo actual
  const [threads, setThreads] = useState([]);
  const [newThread, setNewThread] = useState("");
  <div>
  <h2>Foro</h2>
  <div>
    <h3>Título del Hilo</h3>
    <p>Contenido del hilo...</p>
    <Comments threadId={threadId} /> {/* Pasar el ID del hilo */}
  </div>
</div>
  useEffect(() => {
    const q = query(collection(db, "threads"));
    const unsubscribe = onSnapshot(q, (snapshot) => {
      setThreads(snapshot.docs.map(doc => ({ ...doc.data(), id: doc.id })));
    });
    return () => unsubscribe();
  }, []);

  const addThread = async (e) => {
    e.preventDefault();
    await addDoc(collection(db, "threads"), {
      content: newThread,
      timestamp: new Date(),
    });
    setNewThread("");
  };

  return (
    <div>
      <form onSubmit={addThread}>
        <input
          type="text"
          value={newThread}
          onChange={(e) => setNewThread(e.target.value)}
          placeholder="Nuevo hilo"
        />
        <button type="submit">Añadir Hilo</button>
      </form>
      <div>
        {threads.map((thread) => (
          <div key={thread.id}>
            <h3>{thread.content}</h3>
            <p>Publicado el: {new Date(thread.timestamp.seconds * 1000).toLocaleString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Forum;
